mapper.select:
select

    distinct

'true' as QUERYID,

from ${centreTableName}




    order by ${orderByClause}

select

from ${centreTableName}
where 1=1

  and id = #{id,jdbcType=VARCHAR}


  and table_name = #{tableName,jdbcType=VARCHAR}


  and state = #{state,jdbcType=INTEGER}


  and type = #{type,jdbcType=INTEGER}


  and creattime = #{creattime,jdbcType=TIMESTAMP}


    and audit_status = #{auditStatus,jdbcType=INTEGER}


    and audit_notes = #{auditNotes,jdbcType=VARCHAR}


    and last_check = #{lastCheck,jdbcType=VARCHAR}


  and startdown_time >= #{startdownTimems,jdbcType=VARCHAR}


  and startup_time <= #{startupTimems,jdbcType=VARCHAR}


    and  audit_time = #{auditTime,jdbcType=TIMESTAMP}


     and sync_time = #{syncTime,jdbcType=TIMESTAMP}


     and user_code = #{userCode,jdbcType=VARCHAR}

select
count(*)
from ${centreTableName}
where 1=1

  and id = #{id,jdbcType=VARCHAR}


  and table_name = #{tableName,jdbcType=VARCHAR}


  and state = #{state,jdbcType=INTEGER}


  and type = #{type,jdbcType=INTEGER}


  and creattime = #{creattime,jdbcType=TIMESTAMP}


      and audit_status = #{auditStatus,jdbcType=INTEGER}


      and audit_notes = #{auditNotes,jdbcType=VARCHAR}


      and last_check = #{lastCheck,jdbcType=VARCHAR}


  and startdown_time >= #{startdownTimems,jdbcType=VARCHAR}


  and startup_time <= #{startupTimems,jdbcType=VARCHAR}


      and  audit_time = #{auditTime,jdbcType=TIMESTAMP}


      and sync_time = #{syncTime,jdbcType=TIMESTAMP}


      and user_code = #{userCode,jdbcType=VARCHAR}

select * from (SELECT table_name,state,audit_status,audit_notes,audit_time,sync_time,user_code
FROM ${centreTableName}
WHERE 1=1

    and table_name LIKE concat('%',#{tableName,jdbcType=VARCHAR},'%')


    and state = #{state,jdbcType=INTEGER}


    and type = #{type,jdbcType=INTEGER}


    and audit_status = #{auditStatus,jdbcType=INTEGER}


    and audit_notes LIKE concat('%',#{auditNotes,jdbcType=VARCHAR},'%')


    AND date_format(startdown_time,'%Y-%m')= #{startdownTime,jdbcType=VARCHAR}


    and  audit_time = #{auditTime,jdbcType=TIMESTAMP}


    and sync_time = #{syncTime,jdbcType=TIMESTAMP}


    and user_code = #{userCode,jdbcType=VARCHAR}

) a GROUP by a.table_name

select count(*) from (select * from (SELECT table_name,state,audit_status,audit_notes,audit_time,sync_time,user_code
FROM ${centreTableName}
WHERE 1=1

    and table_name LIKE concat('%',#{tableName,jdbcType=VARCHAR},'%')


    and state = #{state,jdbcType=INTEGER}


    and type = #{type,jdbcType=INTEGER}


    and audit_status = #{auditStatus,jdbcType=INTEGER}


    and audit_notes LIKE concat('%',#{auditNotes,jdbcType=VARCHAR},'%')


    AND date_format(startdown_time,'%Y-%m')= #{startdownTime,jdbcType=VARCHAR}


    and  audit_time = #{auditTime,jdbcType=TIMESTAMP}


    and sync_time = #{syncTime,jdbcType=TIMESTAMP}


    and user_code = #{userCode,jdbcType=VARCHAR}

) a GROUP by a.table_name) b

select
count(*)
from ${centreTableName}
where 1=1

    and state = #{state,jdbcType=INTEGER}


    and audit_status = #{auditStatus,jdbcType=INTEGER}
