mapper.select:
select

    distinct

'true' as QUERYID,

from ${centreTableName}




    order by ${orderByClause}

select

from ${centreTableName}
where 1=1

  and id = #{id,jdbcType=VARCHAR}


  and table_name = #{tableName,jdbcType=VARCHAR}


  and state = #{state,jdbcType=INTEGER}


  and type = #{type,jdbcType=INTEGER}


  and creattime = #{creattime,jdbcType=TIMESTAMP}


    and audit_status = #{auditStatus,jdbcType=INTEGER}


    and audit_notes = #{auditNotes,jdbcType=VARCHAR}


    and last_check = #{lastCheck,jdbcType=VARCHAR}


  and startdown_time >= #{startdownTimems,jdbcType=VARCHAR}


  and startup_time <= #{startupTimems,jdbcType=VARCHAR}


    and  audit_time = #{auditTime,jdbcType=TIMESTAMP}


     and sync_time = #{syncTime,jdbcType=TIMESTAMP}


     and user_code = #{userCode,jdbcType=VARCHAR}


      and data_month = #{dataMonth,jdbcType=VARCHAR}


      and sync_type = #{syncType,jdbcType=INTEGER}


     and check_status = #{checkStatus,jdbcType=INTEGER}


     and applyer_code = #{applyerCode,jdbcType=VARCHAR}


     and auditer_code = #{auditerCode,jdbcType=VARCHAR}


     and syncer_code = #{syncerCode,jdbcType=VARCHAR}


     and pid = #{pid,jdbcType=VARCHAR}


     and data_times = #{dataTimes,jdbcType=VARCHAR}


     and audit_begintime = #{auditBegintime,jdbcType=TIMESTAMP}


     and sync_begintime = #{syncBegintime,jdbcType=TIMESTAMP}

  order by startdown_time

select
count(*)
from ${centreTableName}
where 1=1

  and id = #{id,jdbcType=VARCHAR}


  and table_name = #{tableName,jdbcType=VARCHAR}


  and state = #{state,jdbcType=INTEGER}


  and type = #{type,jdbcType=INTEGER}


  and creattime = #{creattime,jdbcType=TIMESTAMP}


      and audit_status = #{auditStatus,jdbcType=INTEGER}


      and audit_notes = #{auditNotes,jdbcType=VARCHAR}


      and last_check = #{lastCheck,jdbcType=VARCHAR}


  and startdown_time >= #{startdownTimems,jdbcType=VARCHAR}


  and startup_time <= #{startupTimems,jdbcType=VARCHAR}


      and  audit_time = #{auditTime,jdbcType=TIMESTAMP}


      and sync_time = #{syncTime,jdbcType=TIMESTAMP}


      and user_code = #{userCode,jdbcType=VARCHAR}


      and data_month = #{dataMonth,jdbcType=VARCHAR}


      and sync_type = #{syncType,jdbcType=INTEGER}


      and check_status = #{checkStatus,jdbcType=INTEGER}


      and applyer_code = #{applyerCode,jdbcType=VARCHAR}


      and auditer_code = #{auditerCode,jdbcType=VARCHAR}


      and syncer_code = #{syncerCode,jdbcType=VARCHAR}


      and pid = #{pid,jdbcType=VARCHAR}


      and data_times = #{dataTimes,jdbcType=VARCHAR}


      and audit_begintime = #{auditBegintime,jdbcType=TIMESTAMP}


      and sync_begintime = #{syncBegintime,jdbcType=TIMESTAMP}

select * from (SELECT table_name,state,audit_status,audit_notes,audit_time,sync_time,user_code, data_month, sync_type, check_status, applyer_code, auditer_code, syncer_code
FROM ${centreTableName}
WHERE 1=1

    and table_name LIKE concat('%',#{tableName,jdbcType=VARCHAR},'%')


    and state = #{state,jdbcType=INTEGER}


    and type = #{type,jdbcType=INTEGER}


    and audit_status = #{auditStatus,jdbcType=INTEGER}


    and audit_notes LIKE concat('%',#{auditNotes,jdbcType=VARCHAR},'%')





    and  audit_time = #{auditTime,jdbcType=TIMESTAMP}


    and sync_time = #{syncTime,jdbcType=TIMESTAMP}


    and  audit_time >= #{auditTimemsF,jdbcType=VARCHAR}


    and  audit_time <= #{auditTimemsL,jdbcType=VARCHAR}


    and sync_time = #{syncTime,jdbcType=TIMESTAMP}


    and sync_time >= #{syncTimemsF,jdbcType=VARCHAR}


    and sync_time <= #{syncTimemsL,jdbcType=VARCHAR}


    and user_code = #{userCode,jdbcType=VARCHAR}


    and data_month = #{dataMonth,jdbcType=VARCHAR}


    and sync_type = #{syncType,jdbcType=INTEGER}


    and check_status = #{checkStatus,jdbcType=INTEGER}


    and applyer_code = #{applyerCode,jdbcType=VARCHAR}


    and auditer_code = #{auditerCode,jdbcType=VARCHAR}


    and syncer_code = #{syncerCode,jdbcType=VARCHAR}


    and pid = #{pid,jdbcType=VARCHAR}


    and data_times = #{dataTimes,jdbcType=VARCHAR}


    and audit_begintime = #{auditBegintime,jdbcType=TIMESTAMP}


    and sync_begintime = #{syncBegintime,jdbcType=TIMESTAMP}

) a GROUP by a.table_name

select count(*) from (select * from (SELECT table_name,state,audit_status,audit_notes,audit_time,sync_time,user_code, data_month, sync_type, check_status, applyer_code, auditer_code, syncer_code
FROM ${centreTableName}
WHERE 1=1

    and table_name LIKE concat('%',#{tableName,jdbcType=VARCHAR},'%')


    and state = #{state,jdbcType=INTEGER}


    and type = #{type,jdbcType=INTEGER}


    and audit_status = #{auditStatus,jdbcType=INTEGER}


    and audit_notes LIKE concat('%',#{auditNotes,jdbcType=VARCHAR},'%')


    AND date_format(startdown_time,'%Y-%m')= #{startdownTime,jdbcType=VARCHAR}


    and  audit_time = #{auditTime,jdbcType=TIMESTAMP}


    and sync_time = #{syncTime,jdbcType=TIMESTAMP}


    and  audit_time >= #{auditTimemsF,jdbcType=VARCHAR}


    and  audit_time <= #{auditTimemsL,jdbcType=VARCHAR}


    and sync_time = #{syncTime,jdbcType=TIMESTAMP}


    and sync_time >= #{syncTimemsF,jdbcType=VARCHAR}


    and sync_time <= #{syncTimemsL,jdbcType=VARCHAR}


    and user_code = #{userCode,jdbcType=VARCHAR}


    and data_month = #{dataMonth,jdbcType=VARCHAR}


    and sync_type = #{syncType,jdbcType=INTEGER}


    and check_status = #{checkStatus,jdbcType=INTEGER}


    and applyer_code = #{applyerCode,jdbcType=VARCHAR}


    and auditer_code = #{auditerCode,jdbcType=VARCHAR}


    and syncer_code = #{syncerCode,jdbcType=VARCHAR}


    and pid = #{pid,jdbcType=VARCHAR}


    and data_times = #{dataTimes,jdbcType=VARCHAR}


    and audit_begintime = #{auditBegintime,jdbcType=TIMESTAMP}


    and sync_begintime = #{syncBegintime,jdbcType=TIMESTAMP}

) a GROUP by a.table_name) b

select
count(*)
from ${centreTableName}
where 1=1

    and state = #{state,jdbcType=INTEGER}


    and audit_status = #{auditStatus,jdbcType=INTEGER}

SELECT
table_name tableName,
COUNT(*) count
FROM ${centreTableName}
where 1=1

    and id = #{id,jdbcType=VARCHAR}


    and table_name = #{tableName,jdbcType=VARCHAR}

    and state <> 2

    and type = #{type,jdbcType=INTEGER}


    and creattime = #{creattime,jdbcType=TIMESTAMP}


    and audit_status = #{auditStatus,jdbcType=INTEGER}


    and audit_notes = #{auditNotes,jdbcType=VARCHAR}


    and last_check = #{lastCheck,jdbcType=VARCHAR}


    and startdown_time >= #{startdownTimems,jdbcType=VARCHAR}


    and startup_time <= #{startupTimems,jdbcType=VARCHAR}


    and  audit_time = #{auditTime,jdbcType=TIMESTAMP}


    and sync_time = #{syncTime,jdbcType=TIMESTAMP}


    and user_code = #{userCode,jdbcType=VARCHAR}


    and data_month = #{dataMonth,jdbcType=VARCHAR}


    and sync_type = #{syncType,jdbcType=INTEGER}


    and check_status = #{checkStatus,jdbcType=INTEGER}


    and applyer_code = #{applyerCode,jdbcType=VARCHAR}


    and auditer_code = #{auditerCode,jdbcType=VARCHAR}


    and syncer_code = #{syncerCode,jdbcType=VARCHAR}


    and pid = #{pid,jdbcType=VARCHAR}


    and data_times = #{dataTimes,jdbcType=VARCHAR}


    and audit_begintime = #{auditBegintime,jdbcType=TIMESTAMP}


    and sync_begintime = #{syncBegintime,jdbcType=TIMESTAMP}

GROUP BY table_name

