mapper.select:
select

from ${tableName}
where 1=1

  and enable_kerberos = #{enableKerberos,jdbcType=VARCHAR}


  and health = #{health,jdbcType=VARCHAR}


  and name = #{name,jdbcType=VARCHAR}


  and health_checks_id = #{healthChecksId,jdbcType=VARCHAR}


  and cluster_id = #{clusterId,jdbcType=INTEGER}


  and id = #{id,jdbcType=INTEGER}


  and type = #{type,jdbcType=VARCHAR}


  and busy = #{busy,jdbcType=VARCHAR}





  and creattime = #{creattime,jdbcType=TIMESTAMP}


  and services_id = #{servicesId,jdbcType=VARCHAR}


  and last_check >= #{startTime,jdbcType=VARCHAR}


  and last_check <= #{stopTime,jdbcType=VARCHAR}



  and task_time >= #{taskTimeStart,jdbcType=VARCHAR}


  and task_time <= #{taskTimeEnd,jdbcType=VARCHAR}



  and task_time = #{taskTime,jdbcType=VARCHAR}

select
services_id, enable_kerberos, health, name, health_checks_id,
cluster_id, id, type, busy, creattime, last_check,task_time
from ${tableName}
WHERE 1=1

   and task_time >= #{taskTimeStart,jdbcType=VARCHAR}


  and task_time <= #{taskTimeEnd,jdbcType=VARCHAR}



  and DATE_FORMAT(task_time,'%Y%m%d%H%i%s') LIKE CONCAT('%','${secondStart}')



  OR DATE_FORMAT(task_time,'%Y%m%d%H%i%s') LIKE CONCAT('%','${secondEnd}')
